		margin-bottom: 20px;
	}

	&__date {
		width: 320px;
		display: flex;
		margin-bottom: 21px;
		position: relative;
		flex-flow: row wrap;
		justify-content: space-between;

		&__h3 {
			width: 47%;
		}
	}

	&__men {
		margin-top: 20px;
		margin-bottom: 19px;
	}

	&__total {
		display: flex;
		justify-content: space-between;
		margin-top: 30px;
		margin-bottom: 24px;
		height: 19px;

	}

	&__total-text {
		background-color: #fff;
	}

	&__total-price {
		background-color: #fff;
	}

	&__total-between {
		flex-grow: 1;
		margin: 0 5px;
		border-bottom: 1px dotted $color-ds25;
	}
}

.room-number {

	&__symbol {
		font-size: 14px;
	}

	&__number {
		font-size: 24px;
		padding-left: 4px;
		padding-right: 6px;
	}

	&__type {
		color: $color-purple;
	}
}

.price-per-day {
	color: $color-ds50;
	line-height: 22px;

	&__text {
		font-weight: normal;
		font-size: 12px;
		line-height: 18px;
		margin-left: 8px;
	}
}

.room-price-list {
	margin-left: 1px;

	&__item-price {
		min-width: 80px;
		text-align: right;
	}

	&__item {
		display: flex;
		justify-content: space-between;
		margin-bottom: 10px;
	}

	&__item-text {
		position: relative;
		padding-right: 22px;
		display: flex;
		flex-grow: 1;

		&--lower {
			line-height: 18px;
			margin-top: 2px;
		}
	}
}

.service-info {
	position: absolute;
	top: 0;
	right: 0;
	width: 20px;
	height: 20px;
	border: 1px solid $color-ds25;
	box-sizing: border-box;
	border-radius: 10px;
	text-align: center;
	padding-bottom: 3px;
	color: $color-ds25;
	font-weight: bold;
	font-size: 12px;
	line-height: 20px;
}

sources.components\\room-card\\room-card.scss:
.room-card {
	width: 270px;
	background: #fff;
	box-sizing: border-box;
	box-shadow: 0px 10px 20px rgba(31, 32, 65, 0.05);
	border-radius: 4px;
	margin-bottom: 40px;
	overflow: hidden;
	padding-bottom: 20px;

	&__header {
		height: 150px;
		position: relative;
	}

	&__header-shadow {
		position: absolute;
		top: 0;
		left: 0;
		bottom: 0;
		right: 0;
		content: "";
		background: linear-gradient(to right, rgba(0, 0, 0, 0.5) 0%, rgba(0, 0, 0, 0) 15%, rgba(0, 0, 0, 0) 85%, rgba(0, 0, 0, 0.5) 100%);
	}

	&__header-arrow-left,
	&__header-arrow-right {
		position: absolute;
		top: 53px;
		left: 8px;
		font-size: 24px;
		line-height: 24px;
		color: #fff;
		padding: 10px;
		cursor: pointer;
		transition: all 0.4s ease;
	}

	&__header-arrow-right {
		left: auto;
		right: 8px;
	}

	&__header-pagination {
		position: absolute;
		right: 15px;
		bottom: 7px;
	}

	&__title {
		@extend .room-price__title;
		border-bottom: 1px solid $color-ds5;
		padding-top: 26px;
		padding-bottom: 11px;
		margin-left: 20px;
		margin-right: 17px;
		margin-bottom: 0;
	}

	&__feedback {
		margin: 8px 20px 0 22px;
		display: flex;
		justify-content: space-between;
		color: $color-ds50;
		font-size: 14px;
		line-height: 18px;
		align-items: flex-end;
	}

	&__feedback-comments {
		margin-bottom: 2px;

		span {
			font-weight: bold;
			color: $color-ds50;
		}
	}
}

.room-number__number--small {
	font-size: 19px;
}

.price-per-day--lh-17 {
	line-height: 17px;
}

.room-card-pagination-item {
	display: inline-block;
	width: 7px;
	height: 7px;
	border-radius: 4px;
	border: 1px solid #fff;
	margin-left: 4px;
	cursor: pointer;

	&--active {
		background-color: #fff;
	}
}

