  }

  &__input-btn {
    display: flex;
    align-items: center;
    position: relative;
    width: 100%;
    height: 44px;
    padding: 0 15px;
    border: 1px solid $color-ds25;
    box-sizing: border-box;
    border-radius: 4px;
    background-color: white;
    text-align: left;
    color: $color-ds75;
    cursor: pointer;

    &::placeholder {
      color: $color-ds25;
    }

    &:hover,
    &:focus {
      border: 1px solid $color-ds50;
      color: $color-ds75;
    }
  }

  &__glyphicon {
    position: absolute;
    top: 18px;
    right: 16px;
    width: 8px;
    height: 8px;
    border-left: 2px solid $color-ds50;
    border-bottom: 2px solid $color-ds50;
    pointer-events: none;
    transform: rotate(-45deg);
  }

  ._active {
    border-color: $color-ds75;
  }

  &-menu {
    position: absolute;
    display: none;
    max-width: 320px;
    width: 89.5%;
    padding: 13px 7px 13px 15px;
    border: 1px solid $color-ds50;
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
    margin-top: -1px;
    background: #fff;
    z-index: 10000;
  }

  .menu-open {
    display: block;
  }

  .input-btn {
    position: relative;
    text-align: left;
    line-height: 18px;
    padding-right: 44px;
    border-radius: 4px;

    &:hover {
      cursor: pointer;
    }

    .glyphicon {
      top: 18px;
    }
  }

  &-menu-option {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    margin-bottom: 7px;
  }

  &-item-display,
  &-item-controls {
    display: flex;
    align-items: center;
    justify-content: space-between;
    color: $color-ds100;
  }

  &-item-controls {
    width: 92px;

    .button-decrement,
    .button-increment {

      width: 30px;
      height: 30px;
      border: 1px solid $color-ds50;
      border-radius: 50%;
      background-color: white;
      // background: url("assets/img/css_sprites.png") no-repeat -120px -81px;

      &:hover {
        cursor: pointer;
      }
    }

    .icon-decrement::before {
      content: '-';
      font-weight: bold;
    }

    .icon-increment::before {
      content: '+';
      font-weight: bold;
      font-style: normal;

    }

    .button-decrement {
      border-color: $color-ds25;
      background-position: -161px 4px;
    }

    .counter {
      font-size: 12px;
      font-weight: bold;
    }
  }

  &-menu-buttons {
    display: flex;
    justify-content: space-between;
    flex-direction: row-reverse;
    margin-top: 12px;

    .iq-button {
      padding: 0;
      font-weight: bold;
      font-size: 12px;
      line-height: 15px;
      text-transform: uppercase;
      background-color: white;

      &:hover {
        cursor: pointer;
      }
    }

    .btn-clear {
      display: none;
      color: $color-ds50;
    }

    .btn-apply {
      color: $color-purple;
    }
  }
}

.isSelectionRooms {
  .iqdropdown__input-btn {
    width: 266px;
  }

  .iqdropdown-menu {
    width: 266px;
  }
}

